class WeatherCrawler:
    """A class to get weather information for a given city using a weather API."""

    def __init__(self, api_key=None):
        """
        Initialize the weather crawler with optional API key.

        If no API key is provided, the class will use a free API that doesn't require authentication.
        """
        self.api_key = api_key
        self.session = requests.Session()
        self.headers = {
            'User-Agent': 'WeatherCrawler/1.0',
            'Accept': 'application/json',
        }

    def get_weather(self, city):
        """
        Get weather information for the specified city.

        Args:
            city (str): The name of the city to get weather for

        Returns:
            dict: Weather information including temperature, conditions, etc.
        """
        try:
            # Use OpenWeatherMap API if API key is provided
            if self.api_key:
                return self._get_weather_openweathermap(city)
            # Otherwise use a free API that doesn't require authentication
            else:
                return self._get_weather_weatherapi(city)

        except requests.RequestException as e:
            return {"error": f"Request error: {str(e)}"}
        except Exception as e:
            return {"error": f"An error occurred: {str(e)}"}

    def _get_weather_openweathermap(self, city):
        """
        Get weather using OpenWeatherMap API.

        Args:
            city (str): The name of the city

        Returns:
            dict: Weather information
        """
        url = f"https://api.openweathermap.org/data/2.5/weather"
        params = {
            'q': city,
            'appid': self.api_key,
            'units': 'metric'  # Use metric units by default
        }

        response = self.session.get(url, params=params, headers=self.headers)
        response.raise_for_status()

        data = response.json()

        # Format the response
        weather_data = {
            "city": data['name'],
            "timestamp": datetime.now().strftime("%Y-%m-%d %H:%M:%S"),
            "temperature": {
                "value": round(data['main']['temp']),
                "unit": "C"
            },
            "condition": data['weather'][0]['description'],
            "humidity": data['main']['humidity'],
            "wind": {
                "speed": data['wind']['speed'],
                "unit": "m/s"
            }
        }

        return weather_data

    def _get_weather_weatherapi(self, city):
        """
        Get weather using WeatherAPI.com's free tier.

        Args:
            city (str): The name of the city

        Returns:
            dict: Weather information
        """
        # Use weatherapi.com which has a free tier without authentication
        url = f"https://wttr.in/{city.replace(' ', '+')}?format=j1"

        response = self.session.get(url, headers=self.headers)
        response.raise_for_status()

        try:
            data = response.json()

            # Format the response
            current = data['current_condition'][0]
            location = data['nearest_area'][0]

            weather_data = {
                "city": f"{location['areaName'][0]['value']}, {location['country'][0]['value']}",
                "timestamp": datetime.now().strftime("%Y-%m-%d %H:%M:%S"),
                "temperature": {
                    "value": int(current['temp_C']),
                    "unit": "C"
                },
                "condition": current['weatherDesc'][0]['value'],
                "humidity": int(current['humidity']),
                "wind": {
                    "speed": float(current['windspeedKmph']),
                    "unit": "km/h"
                }
            }

            # Add forecast if available
            if 'weather' in data:
                forecast = []
                for day in data['weather']:
                    date = day['date']
                    day_name = datetime.strptime(date, '%Y-%m-%d').strftime('%A')
                    forecast.append({
                        "day": day_name,
                        "temperature": f"{day['mintempC']}°C - {day['maxtempC']}°C"
                    })

                if forecast:
                    weather_data["forecast"] = forecast

            return weather_data

        except (ValueError, KeyError) as e:
            # If JSON parsing fails or expected keys are missing, try the simpler API
            return self._get_weather_simple(city)

    def _get_weather_simple(self, city):
        """
        Get weather using a simpler API format from wttr.in.

        Args:
            city (str): The name of the city

        Returns:
            dict: Weather information
        """
        url = f"https://wttr.in/{city.replace(' ', '+')}?format=%l|%C|%t|%h|%w"

        response = self.session.get(url, headers=self.headers)
        response.raise_for_status()

        # Parse the response which is in format: Location|Condition|Temperature|Humidity|Wind
        parts = response.text.strip().split('|')

        if len(parts) >= 5:
            # Extract temperature value and unit
            temp_str = parts[2].replace('+', '')
            temp_match = temp_str.strip()
            temp_value = int(''.join(filter(lambda x: x.isdigit() or x == '-', temp_match)))

            # Extract wind speed
            wind_parts = parts[4].split(' ')
            wind_speed = float(''.join(filter(lambda x: x.isdigit() or x == '.', wind_parts[0])))
            wind_unit = ''.join(filter(lambda x: x.isalpha(), wind_parts[0]))

            weather_data = {
                "city": parts[0],
                "timestamp": datetime.now().strftime("%Y-%m-%d %H:%M:%S"),
                "temperature": {
                    "value": temp_value,
                    "unit": "C"
                },
                "condition": parts[1],
                "humidity": int(parts[3].replace('%', '')),
                "wind": {
                    "speed": wind_speed,
                    "unit": wind_unit
                }
            }

            return weather_data
        else:
            return {
                "city": city,
                "timestamp": datetime.now().strftime("%Y-%m-%d %H:%M:%S"),
                "error": "Could not parse weather data"
            }"}

