    def _get_weather_openweathermap(self, city):
        """
        Get weather using OpenWeatherMap API.

        Args:
            city (str): The name of the city

        Returns:
            dict: Weather information
        """
        url = f"https://api.openweathermap.org/data/2.5/weather"
        params = {
